                        <p className="text-gray-600 text-sm font-medium">{provider.professionalTitle}</p>
                        {provider.lastActive && (
                          <p className="text-red-500 text-sm">üïí {provider.lastActive}</p>
                        )}
                      </div>
                    </div>
                    <div className="flex justify-center">
                      <div
                        className={`px-6 py-2 rounded-xl font-bold text-sm text-center shadow-lg transform hover:scale-105 transition-all duration-300 ${
                          provider.status === "Active"
                            ? "bg-gradient-to-r from-green-500 to-emerald-500 text-white"
                            : provider.status === "Inactive"
                            ? "bg-gradient-to-r from-gray-400 to-gray-500 text-white"
                            : "bg-gradient-to-r from-gray-400 to-gray-500 text-white"
                        }`}
                      >
                        {provider.status}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Modern Logout Modal */}
      {showLogoutModal && (
        <div className="fixed inset-0 backdrop-blur-lg bg-black/30 flex items-center justify-center z-50">
          <div className="backdrop-blur-xl bg-white/20 border border-white/30 rounded-3xl p-8 max-w-md w-full mx-4 shadow-2xl">
            <h2 className="text-2xl font-bold text-center mb-4 text-gray-800">Logout Confirmation</h2>
            <p className="text-center text-gray-600 mb-6">Are you sure you want to log out of JustGo Health?</p>
            <div className="flex gap-4">
              <button
                onClick={handleLogout}
                className="flex-1 py-3 text-center font-bold text-lg bg-gradient-to-r from-red-500 to-pink-500 text-white rounded-xl hover:from-red-600 hover:to-pink-600 transition-all duration-300"
              >
                Yes, Logout
              </button>
              <button
                onClick={() => setShowLogoutModal(false)}
                className="flex-1 py-3 text-center font-bold text-lg backdrop-blur-sm bg-white/20 border border-white/30 text-gray-700 rounded-xl hover:bg-white/30 transition-all duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
