import { useForm, type UseFormProps, type FieldValues } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { type z } from "zod";

export function useSelectedValuesFromReactHookForm<T extends FieldValues>(
  schema: z.ZodSchema<T>,
  options: Omit<UseFormProps<T>, "resolver"> = {}
) {
  const form = useForm<T>({
    resolver: zodResolver(schema as any),
    ...options,
  });

  return {
    register: form.register,
    handleSubmit: form.handleSubmit,
    errors: form.formState.errors,
    formState: form.formState,
    watch: form.watch,
    setValue: form.setValue,
    getValues: form.getValues,
  } as const;
}

import { api } from "@/lib/api";
import { ENDPOINTS } from "@/constants/endpoints";
import { useMutation } from "@tanstack/react-query";

const useAuthQuery = () => {
  const loginMutation = useMutation({
    mutationKey: ["auth", "login"],
    mutationFn: (data: { email: string; password: string }) => login(data),
  });

  const signupMutation = useMutation({
    mutationKey: ["auth", "signup"],
    mutationFn: (data: {
      email: string;
      password: string;
      fullName: string;
      professionalTitle: string;
    }) => signup(data),
  });

  const verifyEmailMutation = useMutation({
    mutationKey: ["auth", "verifyEmail"],
    mutationFn: (data: { email: string; code: string }) => verifyEmail(data),
  });

  const checkEmailMutation = useMutation({
    mutationKey: ["auth", "checkEmail"],
    mutationFn: (data: { email: string }) => checkEmail(data),
  });

  const recoverAccountMutation = useMutation({
    mutationKey: ["auth", "recoverAccount"],
    mutationFn: (data: { email: string }) => recoverAccount(data),
  });

  const submitRecoveryCodeMutation = useMutation({
    mutationKey: ["auth", "submitRecoveryCode"],
    mutationFn: (data: { code: string; email: string }) =>
      submitAccountRecoveryCode(data),
  });

  const resetPasswordMutation = useMutation({
    mutationKey: ["auth", "resetPassword"],
    mutationFn: (data: { password: string; confirmPassword: string }) =>
      resetPassword(data),
  });

  const addPhotoMutation = useMutation({
    mutationKey: ["auth", "addPhoto"],
    mutationFn: (data: FormData) => addPhoto(data),
  });

  const updateProfileMutation = useMutation({
    mutationKey: ["auth", "updateProfile"],
    mutationFn: (data: {
      officePhoneNumber?: string;
      status?: string;
      [key: string]: any;
    }) => updateProfile(data),
  });

  const addSpecialtyMutation = useMutation({
    mutationKey: ["auth", "addSpecialty"],
    mutationFn: (data: { specialty: string }) => addSpecialty(data),
  });

  async function login(data: { email: string; password: string }) {
    return api(ENDPOINTS.login, { method: "POST", body: JSON.stringify(data) });
  }

  async function signup(data: {
    email: string;
    password: string;
    fullName: string;
    professionalTitle: string;
  }) {
    return api(ENDPOINTS.signup, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  async function checkEmail(data: { email: string }) {
    return api(ENDPOINTS.checkEmail, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  async function verifyEmail(data: { email: string; code: string }) {
    return api(ENDPOINTS.verifyEmail, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  async function recoverAccount(data: { email: string }) {
    return api(ENDPOINTS.recoverAccount, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  async function submitAccountRecoveryCode(data: {
    code: string;
    email: string;
  }) {
    return api(ENDPOINTS.submitAccountRecoveryCode, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  async function resetPassword(data: {
    password: string;
    confirmPassword: string;
  }) {
    return api(ENDPOINTS.resetPassword, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  async function addPhoto(data: FormData) {
    return api(ENDPOINTS.addPhoto, { method: "POST", body: data });
  }

  async function updateProfile(data: {
    officePhoneNumber?: string;
    status?: string;
    [key: string]: any;
  }) {
    return api(ENDPOINTS.updateProfile, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  async function addSpecialty(data: { specialty: string }) {
    return api(ENDPOINTS.addSpecialty, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  return {
    loginMutation,
    signupMutation,
    verifyEmailMutation,
    checkEmailMutation,
    recoverAccountMutation,
    submitRecoveryCodeMutation,
    resetPasswordMutation,
    addPhotoMutation,
    updateProfileMutation,
    addSpecialtyMutation,
  };
};

export default useAuthQuery;

import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  axiosFormData,
  axiosInstance,
  checkResponse,
} from "@/configs/axiosInstance";
import { api } from "@/lib/api";
import { toast } from "sonner";
import { ICreateSchool, School } from "@/types/school";
import { ENDPOINTS } from "@/constants/endpoints";
import { ILockIn } from "@/types/lockin.type";

// API functions
const getSchools = async (): Promise<School[]> => {
  const result = await api("/api/v1/schools");
  return result.data;
};

const getSchool = async (schoolId: string): Promise<School> => {
  const response = await axiosInstance.get(`/api/v1/schools/${schoolId}`);
  return checkResponse(response, 200)?.data;
};

const getSchoolLockIn = async (schoolId: string): Promise<ILockIn> => {
  const response = await axiosInstance.get(ENDPOINTS.getSchoolLockIn(schoolId));
  return checkResponse(response, 200);
};

const createSchool = async (data: ICreateSchool): Promise<School> => {
  const response = await axiosFormData.post(`/api/v1/schools`, data);
  return checkResponse(response, 201);
};

const disableSchool = async (schoolId: string): Promise<School> => {
  const result = await api(`/api/v1/schools/${schoolId}/disable`, {
    method: "PUT",
  });
  return result.data;
};

const SCHOOLS_QUERY_KEY = "schools";
const SCHOOLS_LOCKIN_QUERY_KEY = "schools_lockin";

export const useSchoolsQuery = () => {
  const queryClient = useQueryClient();

  // Fetch all schools
  const schoolsQuery = useQuery({
    queryKey: [SCHOOLS_QUERY_KEY],
    queryFn: getSchools,
  });

  // Get single school lockin
  const getSchoolLockinQuery = (schoolId: string) =>
    useQuery({
      queryKey: [SCHOOLS_LOCKIN_QUERY_KEY, schoolId],
      queryFn: () => getSchoolLockIn(schoolId),
      enabled: !!schoolId,
    });

  // Get single school
  const useSchool = (schoolId: string) =>
    useQuery({
      queryKey: [SCHOOLS_QUERY_KEY, schoolId],
      queryFn: () => getSchool(schoolId),
      enabled: !!schoolId,
    });

  // create school mutation
  const createSchoolMutation = useMutation({
    mutationFn: createSchool,
    onSuccess: (success) => {
      console.log(success);
      queryClient.invalidateQueries({ queryKey: [SCHOOLS_QUERY_KEY] });

      toast.success("School created successfully");
    },
    onError: (error: Error) => {
      toast.error(error.message || "Failed to create school");
    },
  });

  // Disable school mutation
  const disableSchoolMutation = useMutation({
    mutationFn: disableSchool,
    onSuccess: (data, schoolId) => {
      // Invalidate schools queries to trigger refetch
      queryClient.invalidateQueries({ queryKey: [SCHOOLS_QUERY_KEY] });
      queryClient.invalidateQueries({
        queryKey: [SCHOOLS_QUERY_KEY, schoolId],
      });

      toast.success("School disabled successfully");
    },
    onError: (error: Error) => {
      toast.error(error.message || "Failed to disable school");
    },
  });

  // Return all queries and mutations
  return {
    // Queries
    schools: schoolsQuery.data,
    isLoading: schoolsQuery.isLoading,
    isError: schoolsQuery.isError,
    error: schoolsQuery.error,
    // Single school query helper
    useSchool,
    getSchoolLockinQuery,
    // Mutations
    createSchoolMutation,
    disableSchool: disableSchoolMutation.mutate,
    isDisabling: disableSchoolMutation.isPending,
  };
};

const API_BASE =
  process.env.NODE_ENV === "development"
    ? "" // Use proxy in development
    : process.env.NEXT_PUBLIC_API_BASE_URL ||
    "https://justgo.up.railway.app";

export async function api(endpoint: string, options: RequestInit = {}) {
  const token = localStorage.getItem("token");

  const headers: Record<string, string> = {
    ...(options.headers as Record<string, string>),
  };

  // Only set Content-Type to application/json if body is not FormData
  if (!(options.body instanceof FormData)) {
    headers["Content-Type"] = "application/json";

    if (
      options.body &&
      typeof options.body === "object" &&
      !(options.body instanceof Blob) &&
      !(options.body instanceof URLSearchParams)
    ) {
      options.body = JSON.stringify(options.body);
    }
  }

  const publicEndpoints = [
    "/api/v1/auth/check-email",
    "/api/v1/auth/curator/check-email",
    "/api/v1/auth/sign-in",
    "/api/v1/auth/create-account",
    "/api/v1/auth/curator/sign-in",
    "/api/v1/auth/submit-signup-code",
    "/api/v1/auth/recover-account",
    "/api/v1/auth/submit-account-recovery-code",
    "/api/v1/auth/reset-password",
  ];

  const isPublicEndpoint = publicEndpoints.some((path) =>
    endpoint.includes(path)
  );

  if (token && !headers.Authorization && !isPublicEndpoint) {
    headers.Authorization = `Bearer ${token}`;
  }

  try {
    const response = await fetch(`${API_BASE}${endpoint}`, {
      ...options,
      headers,
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || "API request failed");
    }
    return response.json();
  } catch (error: any) {
    throw new Error(error?.message || "Network error");
  }
}
